function postJSON(n,t,s,a){$.ajax({url:n,type:"POST",data:JSON.stringify(t),contentType:"application/json",success:s,error:()=>alert(a)})}function addQuestion(n,t,s){postJSON("/qnastack/question/"+n+"/add",t,s,"등록 실패했습니다. 페이지 새로고침 후 다시 시도해 주세요.")}function editQuestion(n,t,s){postJSON("/qnastack/question/"+n+"/edit",t,s,"수정 실패했습니다. 페이지 새로고침 후 다시 시도해 주세요.")}function delQuestion(n,t,s){postJSON("/qnastack/question/"+n+"/del",t,s,"삭제 실패했습니다. 페이지 새로고침 후 다시 시도해 주세요.")}function addAnswer(n,t){postJSON("/qnastack/answer/add",n,t,"등록 실패했습니다. 페이지 새로고침 후 다시 시도해 주세요.")}function editAnswer(n,t){postJSON("/qnastack/answer/edit",n,t,"수정 실패했습니다. 페이지 새로고침 후 다시 시도해 주세요.")}function delAnswer(n,t){postJSON("/qnastack/answer/del",n,t,"삭제 실패했습니다. 페이지 새로고침 후 다시 시도해 주세요.")}function evaluateAnswer(n,t){postJSON("/qnastack/answer/evaluate",n,t,"평가 실패했습니다. 페이지 새로고침 후 다시 시도해 주세요.")}