function createElement(r){if("string"==typeof r)return document.createTextNode(r);if(Array.isArray(r)){const o=document.createDocumentFragment();for(let e=0,t=r.length;e<t;e++)o.appendChild(createElement(r[e]));return o}const n=document.createElement(r.el);var l=Object.keys(r);for(let e=0,t=l.length;e<t;e++){const s=l[e];if(s.match(/^data-/))n.dataset[s.replace("data-","").replace(/-(\w)/g,e=>e.toUpperCase()[1])]=r[s];else if("children"==s){const f=document.createDocumentFragment(),d=r[s];for(let e=0,t=d.length;e<t;e++)f.appendChild(createElement(d[e]));n.appendChild(f)}else if("style"==s)if("string"==typeof r[s])n[s]=r[s];else if("object"==typeof r[s]){var a=r[s];for(let e=0,t=a.length;e<t;e++){var c=a[e];n[s][c]=a[c]}}else console.error("style에 "+typeof r[s]+"은/는 맞지 않습니다.");else"el"!=s&&(n[s]=r[s])}return n}