function createElement(r){if("string"==typeof r)return document.createTextNode(r);if(Array.isArray(r)){const s=document.createDocumentFragment();for(let e=0,t=r.length;e<t;e++)s.appendChild(createElement(r[e]));return s}const n=document.createElement(r.el);var l=Object.keys(r);for(let e=0,t=l.length;e<t;e++){const i=l[e];if(i.match(/^data-/))n.dataset[i.replace("data-","").replace(/-(\w)/g,e=>e.toUpperCase()[1])]=r[i];else if("children"==i){const d=document.createDocumentFragment(),f=r[i];for(let e=0,t=f.length;e<t;e++)d.appendChild(createElement(f[e]));n.appendChild(d)}else if("style"==i)if("string"==typeof r[i])n[i]=r[i];else if("object"==typeof r[i]){var a=r[i],c=Object.keys(a);for(let e=0,t=c.length;e<t;e++){var o=c[e];n[i][o]=a[o]}}else console.error("style에 "+typeof r[i]+"은/는 맞지 않습니다.");else"el"!=i&&(void 0===n[i]?n.setAttribute(i,r[i]):n[i]=r[i])}return n}