function createElement(r){if("string"==typeof r)return document.createTextNode(r);if(Array.isArray(r)){const s=document.createDocumentFragment();for(let e=0,t=r.length;e<t;e++)s.appendChild(createElement(r[e]));return s}const n=document.createElement(r.el);var l=Object.keys(r);for(let e=0,t=l.length;e<t;e++){const f=l[e];if(f.match(/^data-/))n.dataset[f.replace("data-","").replace(/-(\w)/g,e=>e.toUpperCase()[1])]=r[f];else if("children"==f){const i=document.createDocumentFragment(),d=r[f];for(let e=0,t=d.length;e<t;e++)i.appendChild(createElement(d[e]));n.appendChild(i)}else if("style"==f)if("string"==typeof r[f])n[f]=r[f];else if("object"==typeof r[f]){var a=r[f],c=Object.keys(a);for(let e=0,t=c.length;e<t;e++){var o=c[e];n[f][o]=a[o]}}else console.error("style에 "+typeof r[f]+"은/는 맞지 않습니다.");else"el"!=f&&(null==n[f]?n.setAttribute(f,r[f]):n[f]=r[f])}return n}