function createElement(r){if("string"==typeof r)return document.createTextNode(r);if(Array.isArray(r)){const t=document.createDocumentFragment();return r.forEach(e=>t.append(createElement(e))),t}const a=document.createElement(r.el);return Object.keys(r).forEach(t=>{t.match(/^data-/)?a.dataset[t.replace("data-","").replace(/-(\w)/g,e=>e.toUpperCase()[1])]=r[t]:"children"==t?r[t].forEach(e=>a.appendChild(createElement(e))):"style"==t?"string"==typeof r[t]?a[t]=r[t]:"object"==typeof r[t]?Object.keys(r[t]).forEach(e=>a[t][e]=r[t][e]):console.error("style에 "+typeof r[t]+"은/는 맞지 않습니다."):"el"!=t&&(a[t]=r[t])}),a}