Date.prototype.format=function(e){if(!this.valueOf())return" ";const t=["일요일","월요일","화요일","수요일","목요일","금요일","토요일"],r=this;return e.replace(/(yyyy|yy|MM|dd|E|e|HH|hh|mm|ss|a\/p)/g,function(e){switch(e){case"yyyy":return r.getFullYear();case"yy":return(r.getFullYear()%1e3).zf(2);case"MM":return(r.getMonth()+1).zf(2);case"dd":return r.getDate().zf(2);case"E":return t[r.getDay()];case"e":return t[r.getDay()][0];case"HH":return r.getHours().zf(2);case"hh":return((h=r.getHours()%12)?h:12).zf(2);case"mm":return r.getMinutes().zf(2);case"ss":return r.getSeconds().zf(2);case"a/p":return r.getHours()<12?"오전":"오후";default:return e}})},Date.prototype.getWeek=function(){var e=new Date(this.getFullYear(),0,1);return Math.ceil(((this-e)/864e5+e.getDay()+1)/7)},String.prototype.zf=function(e){return this.padStart(e,0)},Number.prototype.zf=function(e){return this.toString().zf(e)};