Date.prototype.format=function(t){if(!this.valueOf())return" ";const e=["일요일","월요일","화요일","수요일","목요일","금요일","토요일"],r=this;return t.replace(/(yyyy|yy|MM|dd|E|hh|mm|ss|a\/p)/gi,function(t){switch(t){case"yyyy":return r.getFullYear();case"yy":return(r.getFullYear()%1e3).zf(2);case"MM":return(r.getMonth()+1).zf(2);case"dd":return r.getDate().zf(2);case"E":return e[r.getDay()];case"HH":return r.getHours().zf(2);case"hh":return((h=r.getHours()%12)?h:12).zf(2);case"mm":return r.getMinutes().zf(2);case"ss":return r.getSeconds().zf(2);case"a/p":return r.getHours()<12?"오전":"오후";default:return t}})},Date.prototype.getWeek=function(){const t=new Date(this.getFullYear(),0,1);return Math.ceil(((this-t)/864e5+t.getDay()+1)/7)},String.prototype.zf=function(t){return this.padStart(t,0)},Number.prototype.zf=function(t){return this.toString().zf(t)};